  CREATE OR REPLACE FORCE VIEW "JHS"."PRODVIEW" ("PROD_ID", "PROD_NAME", "PROD_LGU", "PROD_BUYER", "PROD_COST", "PROD_PRICE", "PROD_SALE", "PROD_OUTLINE", "PROD_DETAIL", "PROD_IMG", "PROD_TOTALSTOCK", "PROD_INSDATE", "PROD_PROPERSTOCK", "PROD_SIZE", "PROD_COLOR", "PROD_DELIVERY", "PROD_UNIT", "PROD_QTYIN", "PROD_QTYSALE", "PROD_MILEAGE", "LPROD_NM", "BUYER_ID", "BUYER_NAME", "BUYER_LGU", "BUYER_BANK", "BUYER_BANKNO", "BUYER_BANKNAME", "BUYER_ZIP", "BUYER_ADD1", "BUYER_ADD2", "BUYER_COMTEL", "BUYER_FAX", "BUYER_MAIL", "BUYER_CHARGER", "BUYER_TELEXT") AS 
  (

SELECT PROD."PROD_ID",PROD."PROD_NAME",PROD."PROD_LGU",PROD."PROD_BUYER",PROD."PROD_COST",PROD."PROD_PRICE",PROD."PROD_SALE",PROD."PROD_OUTLINE",PROD."PROD_DETAIL",PROD."PROD_IMG",PROD."PROD_TOTALSTOCK",PROD."PROD_INSDATE",PROD."PROD_PROPERSTOCK",PROD."PROD_SIZE",PROD."PROD_COLOR",PROD."PROD_DELIVERY",PROD."PROD_UNIT",PROD."PROD_QTYIN",PROD."PROD_QTYSALE",PROD."PROD_MILEAGE"
     , LPROD_NM
	 , BUYER."BUYER_ID",BUYER."BUYER_NAME",BUYER."BUYER_LGU",BUYER."BUYER_BANK",BUYER."BUYER_BANKNO",BUYER."BUYER_BANKNAME",BUYER."BUYER_ZIP",BUYER."BUYER_ADD1",BUYER."BUYER_ADD2",BUYER."BUYER_COMTEL",BUYER."BUYER_FAX",BUYER."BUYER_MAIL",BUYER."BUYER_CHARGER",BUYER."BUYER_TELEXT"
FROM PROD INNER JOIN  LPROD ON (PROD_LGU = LPROD_GU) 
 INNER JOIN  BUYER ON (PROD_BUYER = BUYER_ID)
 
)
;
