  CREATE OR REPLACE TRIGGER "JHS"."TG_BUYPROD_INSERT" 
    AFTER INSERT ON BUYPROD --저장한 후 
    FOR EACH ROW
DECLARE
    V_PID PROD.PROD_ID%TYPE := (:NEW.BUY_PROD); --매입상품코드
    V_QTY NUMBER := (:NEW.BUY_QTY); -- 매입수량 보관할 곳
