    UPDATE REMAIN A
       SET A.REMAIN_I = A.REMAIN_I + V_QTY,
           A.REMAIN_J_99 = A.REMAIN_J_99 + V_QTY,
           A.REMAIN_DATE = SYSDATE
    WHERE A.PROD_ID  = V_PID;
END;
