--  DDL for Trigger TG_CHANGE_CART
--------------------------------------------------------

  CREATE OR REPLACE TRIGGER "JHS"."TG_CHANGE_CART" 
    AFTER INSERT OR UPDATE OR DELETE ON CART
    FOR EACH ROW
DECLARE
    V_QTY NUMBER := 0;
    V_PID PROD.PROD_ID%TYPE;
    V_DATE DATE;
    V_MID MEMBER.MEM_ID%TYPE;
    V_MILE NUMBER := 0;
BEGIN
    IF INSERTING THEN 
        V_QTY :=(:NEW.CART_QTY);
        V_PID :=(:NEW.CART_PROD);
        V_DATE := TO_DATE(SUBSTR(:NEW.CART_NO,1,8));
        V_MID := (:NEW.CART_MEMBER);
    ELSIF UPDATING THEN 
        V_QTY := (:NEW.CART_QTY) - (:OLD.CART_QTY);
        V_PID := (:NEW.CART_PROD);
        V_DATE := TO_DATE(SUBSTR(:NEW.CART_NO,1,8));
        V_MID := (:NEW.CART_MEMBER);
    ELSIF DELETING THEN
        V_QTY := -(:OLD.CART_QTY);
        V_PID := (:OLD.CART_PROD);
        V_DATE := TO_DATE(SUBSTR(:OLD.CART_NO,1,8));
        V_MID := (:OLD.CART_MEMBER);
    END IF;



    UPDATE REMAIN 
       SET REMAIN_O = REMAIN_O + V_QTY,
           REMAIN_J_99 = REMAIN_J_99 - V_QTY,
           REMAIN_DATE = V_DATE
    WHERE PROD_ID = V_PID;

    SELECT V_QTY*PROD_MILEAGE INTO V_MILE
      FROM PROD
     WHERE PROD_ID = V_PID;

    UPDATE MEMBER 
       SET MEM_MILEAGE = MEM_MILEAGE + V_MILE
     WHERE MEM_ID = V_MID;
END;

/
